var Geoservices = require('../geoservices-js');
var client = new Geoservices();

client.authenticate('jgravois', '10speed', { /* optional options */ }, function (err, results) {
  if (err) {
    console.log("ERROR: "+err);
  }
  else {
    var batch = new client.geocode.Batch();

    for (i=0;i<=2;i++) {
      batch.geocode({
        singleLine: "224 N. College Ave, Bloomington, Indiana",
        OBJECTID: i
      });
    }
    // run the batch
    batch.run(function (err, results) {
      console.dir(JSON.stringify(results));
    });
  }
});
