var Geoservices = require('../geoservices-js');
var client = new Geoservices();

/*
-122.67645787323158, 45.51650314320659

-117.172026,32.706517,-117.152498,32.725514

http://geocode.arcgis.com/arcgis/rest/services/World/GeocodeServer/find?text=920 SW 3rd Ave, Portland, OR 97201&bbox=-117.172026%2C32.706517%2C-117.152498%2C32.725514&f=pjson
*/

client.geocode({
  text: "920 SW 3rd Ave, Portland, OR 97201",
  bbox: "-101,30,-100,31"
}, function (err, result) {
  if (result.locations.length) {
    // remove 'bbox' parameter and a result is returned
    console.log("match found");
  } else {
    console.log("no match found, because portland is near -122, 45");
  }
});
