var Geoservices = require('../geoservices-js');

var client = new Geoservices();

client.authenticate('jgravois', '10speed', { /* optional options */ }, function (err, results) {
  if (err) {
    console.log("ERROR: "+err);
  }
  else {
    var batch = new client.geocode.Batch();

    // add addresses to geocode
    for (i=1;i<=1;i++) {
      batch.geocode("224 N. College Ave, Bloomington, Indiana");
    }

    batch.geocode("completely bunk address that could never match anything");

    // run the batch
    batch.run(function (err, results) {
      console.dir('yer done');
      console.dir(results);
      // console.dir(results.locations.length);
    });
  }
});
